function animateBlur(elem,startSize,endSize,speed){startSize=(startSize+'').replace('px','');$({blurRadius:startSize}).animate({blurRadius:endSize},{duration:4000,easing:'linear', step:function(){$('.container').css({"-webkit-filter":"blur("+endSize+")","filter":"blur("+endSize+")"});}});}
