jQuery.fn.zoomable=function(options){ $(this).each(function(){ options=initOptions(options,$(this)); $('<img/>').src=options.fullsize; var img=$('<img />',{'src':options.fullsize,})
img.css({'max-height':'100%','max-width':'100%','position':'relative','top':'50%','transform':'translateY(-50%)','border':options.border,'border-radius':options.radius,'box-shadow':options.shadow,}); var container=$('<div />',{'class':'zoomable-container','html':img,});container.css({'background-color':options.bgcolor,'padding':options.padding,'text-align':'center','position':'fixed','top':'0','left':'0','z-index':9999999,'height':'100%','width':'100%','display':'none',}); container.css({'background-color':container.css('background-color').replace('rgb','rgba').replace(')','')+', '+options.opacity+')'});container.click(function(){$(this).fadeOut(options.speed);var blurNoPx=options.blur.replace('px','');$({blurRadius:blurNoPx}).animate({blurRadius:0},{duration:options.speed,easing:'swing', step:function(){$('.container').css({"-webkit-filter":"blur("+this.blurRadius+"px)","filter":"blur("+this.blurRadius+"px)"});}}); $('html, body').css({'overflow':'auto','height':'auto'});});$(this).click(function(){container.appendTo('body').fadeIn(options.speed);$({blurRadius:0}).animate({blurRadius:options.blur},{duration:options.speed,easing:'swing', step:function(){$('.container').css({"-webkit-filter":"blur("+this.blurRadius+"px)","filter":"blur("+this.blurRadius+"px)"});}}); $('html, body').css({'overflow':'hidden','height':'100%'});});});};function initOptions(options,parent){ options=$.extend({},$.fn.zoomable.defaults,options);if(parent.attr('fullsize')){ options.fullsize=parent.attr('fullsize');}else{ options.fullsize=parent.attr('src');}
return options;}
$.fn.zoomable.defaults={padding:'30px',bgcolor:'hsla(0, 4%, 3%, 0.38196601)',blur:'3px',speed:300,border:'1px solid hsl(0, 4%, 17%)',radius:'2px',shadow:'0 0 14px hsla(0, 4%, 3%, 0.33)',};